INSERT INTO `f_blog` VALUES ('468f1235bd46498b83f7631eab63812a', 'linux服务器java环境搭建', 'linux java', '# linux服务器 java环境搭建\n## jdk安装\n1. tar -zxvf jdk-8u60-linux-x64.tar.gz\n2. vi /etc/profile\n~~~~\n    //export PATH 上面,添加如下内容：JAVA_HOME根据实际目录来\n    JAVA_HOME=/usr/java/jdk1.8.0_60\n    CLASSPATH=.:$JAVA_HOME/lib/\n    PATH=$JAVA_HOME/bin:$PATH\n    export PATH JAVA_HOME CLASSPATH\n~~~~\n3. source /etc/profile\n4. java -version  //成功输出,安装成功\n\n## mysql安装\n', '20f4fc8d3c694f0e895e1a66c332bfcb', '/blogs/0d76004eccb54b03ab9a07de145f520e/2017-02-26/468f1235bd46498b83f7631eab63812a.md', '/blogs/0d76004eccb54b03ab9a07de145f520e/2017-02-26/468f1235bd46498b83f7631eab63812a.html', '0', '2017-02-26 05:03:08', '0', '0d76004eccb54b03ab9a07de145f520e', '2017-02-21 18:42:23', '0d76004eccb54b03ab9a07de145f520e', '2017-06-28 06:05:19', null, '0', '1', '1'), ('7c1de22e1c1049d49f34ffe3c55b0ebd', 'Java NIO', 'jdk nio', 'java NIO (NEW IO) JDK1.4引入的API，可以代替传统 JAVA IO API .', '20f4fc8d3c694f0e895e1a66c332bfcb', '/blogs/0d76004eccb54b03ab9a07de145f520e/2017-06-20/7c1de22e1c1049d49f34ffe3c55b0ebd.md', '/blogs/0d76004eccb54b03ab9a07de145f520e/2017-06-20/7c1de22e1c1049d49f34ffe3c55b0ebd.html', '0', '2017-06-20 04:32:28', '0', '0d76004eccb54b03ab9a07de145f520e', '2017-06-20 04:09:43', '0d76004eccb54b03ab9a07de145f520e', '2017-06-20 04:32:28', null, '0', '1', null), ('94d3c611bb4d4d15a9c6ff694da30422', 'iptables', 'linux 防火墙', '# iptables\niptables-1.4.21-16.el7.x86_64\n在/etc/sysconfig 下没有 iptables 配置文件\n~~~~\n//配置方式\niptables -A INPUT -p tcp --dport 80 -j ACCEPT\n//保存并查看配置\niptables-save\n//保存配置文件 生成iptable文件\niptables-save > /etc/sysconfig/iptable\n//测试端口\ntelnet ip port \n~~~~\n\n# firewalld\n', '624cef40901045d98c2c2ef11b29e17f', '/blogs/0d76004eccb54b03ab9a07de145f520e/2017-02-26/94d3c611bb4d4d15a9c6ff694da30422.md', '/blogs/0d76004eccb54b03ab9a07de145f520e/2017-02-26/94d3c611bb4d4d15a9c6ff694da30422.html', '0', '2017-02-26 04:58:33', '0', '0d76004eccb54b03ab9a07de145f520e', '2017-02-26 04:25